import { Signer } from "casper-js-sdk";
import React from "react";
import { Router, Route, Routes } from "react-router";
import './style.css'

function App() {


  async function connect(){

    Signer.sendConnectionRequest()

  }
  async function disconnect(){
    Signer.disconnectFromSite();
  }



  return (

 <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
<div className="container-fluid">
 <a className="navbar-brand" href="/Home">Casper Chess</a>

   <ul className="navbar-nav me-auto">
     <li className="nav-item">
       <a className="nav-link" href="/Home">Home</a>
     </li>
     <li className="nav-item">
       <a className="nav-link" href="/Game">Game</a>
     </li>
     <li className="nav-item">
       <a className="nav-link" href="/Settings">Settings</a>
     </li>
     <li className="nav-item">
       <a className="nav-link" href="/Tutorial">Tutorial</a>
     </li>
    <li className="nav-item">
       <button onClick={connect} id="connectBtn">Connect Signer</button>
     </li>
     <li className="nav-item">
       <button onClick={disconnect} id="connectBtn">Disconnect Signer</button>
     </li>
   </ul>
   <ul>
     <li id="addressDisplay">Connected account: </li>
     <li id="balanceDisplay">Balance: </li>
   </ul>
</div>
</nav>


  );
}

export default App;
