import {CasperClient, CasperServiceByJsonRPC, CLPublicKey, isConnected, Signer} from "casper-js-sdk";
import React, { useEffect, useState } from "react";
import { Routes, Route, Link, Outlet } from "react-router-dom";
import './style.css';
import Home from './pages/Home';
import About from './pages/About';
import Settings from './pages/Settings';
import Nopage from './pages/Nopage';
import Game from './pages/Game';
import Layout from './pages/Layout';

const apiUrl = "/api/rpc";
const casperService = new CasperServiceByJsonRPC(apiUrl);
const casperClient = new CasperClient(apiUrl);



function App() {


  const [balance, setBalance] = useState('0');
  const [address, setAddress] = useState('...');



  useEffect(() => {
    async function AccountInformation() {
      const isConnected = await window.casperlabsHelper.isConnected();
      if (isConnected) {
        const publicKey = await window.casperlabsHelper.getActivePublicKey();
        const latestBlock = await casperService.getLatestBlockInfo();
        const root = await casperService.getStateRootHash(
          latestBlock.block.hash
        );

        const balanceUref =
          await casperService.getAccountBalanceUrefByPublicKey(
            root,
            CLPublicKey.fromHex(publicKey)
          );

        //account balance from the last block
        const bal = await casperService.getAccountBalance(
          latestBlock.block.header.state_root_hash,
          balanceUref
        );
        const balance = bal.div(1000000000);
        if (isConnected) {
          setBalance(balance.toString());
        } else {setBalance()}

        if (isConnected) {setAddress(publicKey)} else {setAddress('...')}

      }

    }
    {
      return AccountInformation();
    }

  }, [isConnected]);

  async function connect(){
    try{Signer.sendConnectionRequest().then(console.log('connect'))} catch(error){console.log(error)}

  }
  async function disconnect(){
    try{Signer.disconnectFromSite().then(console.log('disconnect'));} catch(error){console.log(error)}
  }


  return (
    <div>

<nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
    <a className="navbar-brand" href="/Home">Casper Chess</a>

      <ul className="navbar-nav me-auto">
        <Layout />
       <li className="nav-item">
          <a className="nav-link" onClick={connect} >Connect Signer</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" onClick={disconnect} >Disconnect Signer</a>
        </li>
      </ul>

      <ul>
        <li id="addressDisplay">Connected account: {address}</li>
        <li id="balanceDisplay">Balance: {balance}</li>
      </ul>
  </div>
</nav>
      <Routes>
        <Route path="/" element={<Layout />} />
        <Route index element={<Home />} />
        <Route path="/About" element={<About />} />
        <Route path="/Game" element={<Game />} />
        <Route path="/Settings" element={<Settings />} />
        <Route path="*" element={<Nopage />} />
      </Routes>
      <Outlet />
      </div>


  );
}

export default App;
