import {CasperClient, CasperServiceByJsonRPC, CLPublicKey, isConnected, Signer} from "casper-js-sdk";
import React, { useEffect, useState } from "react";
import { Routes, Route, Link, Outlet } from "react-router-dom";
import './style.css';
import Home from './pages/Home';
import About from './pages/About';
import Settings from './pages/Settings';
import Nopage from './pages/Nopage';
import Game from './pages/Game';
import Layout from './pages/Layout';

const apiUrl = "http://localhost:6100/api/rpc";
const casperService = new CasperServiceByJsonRPC(apiUrl);
const casperClient = new CasperClient(apiUrl);



function App() {


  const [balance, setBalance] = useState('');
  const [address, setAddress] = useState('');

  useEffect(()=>{ //set html
    const textAddress = document.getElementById('textAddress');
    const textBalance = document.getElementById('textBalance');
    textAddress.textContent = `Connected account: ${address}`
    textBalance.textBalance = `Balance: ${balance}`
  },[address, balance])

  useEffect(()=>{
    async function AccountInfo(){

      const publicKey = await window.casperlabsHelper.getActivePublicKey();
            setAddress(publicKey);



    }

    var accountinfoint = setInterval(AccountInfo, 5000)
    return accountinfoint;


  },[])

  async function connect(){
    try{Signer.sendConnectionRequest().then(console.log('connect'))

  } catch(error){console.log(error)}

  }
  async function disconnect(){
    try{Signer.disconnectFromSite().then(console.log('disconnect'));} catch(error){console.log(error)}
  }



  async function deploy(){

  }
  return (
    <div>

<nav className="navbar navbar-expand-lg navbar-dark bg-dark">
  <div className="container-fluid">
    <a className="navbar-brand" href="/Home">Casper Chess</a>

      <ul className="navbar-nav me-auto">
        <Layout />
       <li className="nav-item">
          <a className="nav-link" id="connectBtn" onClick={connect} >Connect Signer</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" id="disconnectBtn" onClick={disconnect} >Disconnect Signer</a>
        </li>
        <li className="nav-item">
          <a className="nav-link" id="deployBtn" onClick={deploy} >Save to Chain</a>
        </li>
      </ul>

      <ul>
        <li id="textAddress">Connected account: </li>
        <li id="textBalance">Balance: </li>
      </ul>
  </div>
</nav>
      <Routes>
        <Route path="/" element={<Layout />} />
        <Route index element={<Home />} />
        <Route path="/About" element={<About />} />
        <Route path="/Game" element={<Game />} />
        <Route path="/Settings" element={<Settings />} />
        <Route path="*" element={<Nopage />} />
      </Routes>
      <Outlet />
      </div>


  );
}

export default App;

